// ScrollSpy.js
import React, { useState, useEffect } from 'react';
import Nav from 'react-bootstrap/Nav';


const ScrollSpy = () => {
  const [activeSection, setActiveSection] = useState(null);

  useEffect(() => {
    const handleScroll = () => {
      // Get the scroll position
      const scrollY = window.scrollY || document.documentElement.scrollTop;

      // Define section positions
      const sections = document.querySelectorAll('.section');
      const sectionPositions = [];

      sections.forEach((section) => {
        const sectionTop = section.offsetTop - 50; // Adjust for better accuracy
        const sectionBottom = sectionTop + section.clientHeight;

        sectionPositions.push({ id: section.id, top: sectionTop, bottom: sectionBottom });
      });

      // Determine the active section based on scroll position
      let newActiveSection = null;
      for (const section of sectionPositions) {
        if (scrollY >= section.top && scrollY < section.bottom) {
          newActiveSection = section.id;
          break;
        }
      }

      // Update the active section
      setActiveSection(newActiveSection);
    };

    // Attach event listener on mount
    window.addEventListener('scroll', handleScroll);

    // Clean up the event listener on unmount
    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  return (
    <div>
      <Nav  defaultActiveKey="/home" >
         <Nav.Item  className={activeSection === 'section1' ? 'active' : ''}>
             <Nav.Link href="#section1"><h4>Home</h4></Nav.Link>
         </Nav.Item>
         <Nav.Item className={activeSection === 'section2' ? 'active' : ''}>
              <Nav.Link href="#section2"><h4>About Us</h4></Nav.Link>
         </Nav.Item>
         <Nav.Item className={activeSection === 'section3' ? 'active' : ''}>
             <Nav.Link href="#section3"><h4>Services</h4></Nav.Link>
         </Nav.Item>
         <Nav.Item className={activeSection === 'section4' ? 'active' : ''}>
             <Nav.Link href="#section4"><h4>Services</h4></Nav.Link>
         </Nav.Item>
         <Nav.Item className={activeSection === 'section5' ? 'active' : ''}>
             <Nav.Link href="#section5"><h4>Services</h4></Nav.Link>
         </Nav.Item>
      </Nav>
     
      <div id="section1" className="section">
        Section 1 Content
      </div>
      <div id="section2" className="section">
        Section 2 Content
      </div>
      <div id="section3" className="section">
        Section 3 Content
      </div>
      <div id="section4" className="section">
        Section 4 Content
      </div>
      <div id="section5" className="section">
        Section 5 Content
      </div>
    </div>
  );
};

export default ScrollSpy;
